mapper.select:

SELECT A.* FROM
(
  SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM,
  pet_sitter_house.* FROM
  (
    SELECT *
    FROM pet_sitter_house
    WHERE
    
    idx=#{pet_sitter_house.idx}
    
    AND sregister = 'yes'
    
      AND care_space = #{pet_sitter_houseDto.care_space}
    
    
      AND pick_up = #{pet_sitter_houseDto.pick_up}
    
    
      AND yard = #{pet_sitter_houseDto.yard}
    
    
      AND other_animal = #{pet_sitter_houseDto.other_animal}
    
    
      AND patient_dog = #{pet_sitter_houseDto.patient_dog}
    
    
      AND license = #{pet_sitter_houseDto.license}
    
    
      AND outdoor_bowels = #{pet_sitter_houseDto.outdoor_bowels}
    
    
      AND homemade_snacks = #{pet_sitter_houseDto.homemade_snacks}
    
    
      AND first_aid = #{pet_sitter_houseDto.first_aid}
    
    
      AND medication_possible = #{pet_sitter_houseDto.medication_possible}
    
    
      
        AND care_age LIKE CONCAT('%', #{pet_sitter_houseDto.care_age}, '%')
      
    
    
      
        AND care_size >= #{pet_sitter_houseDto.care_size}
      
    
    
      
        AND house_address LIKE CONCAT(#{pet_sitter_houseDto.area}, '%')
      
    
    ORDER BY idx DESC
  ) pet_sitter_house,
  ( SELECT @ROWNUM := 0 ) R
) A

  WHERE
  A.ROWNUM >= ${start_row} AND A.ROWNUM <= ${end_row}


  SELECT COUNT(*) FROM pet_profile
  WHERE
  user_email=#{user_email}
